DROP TABLE IF EXISTS champions;
CREATE TABLE champions(
   champion_name      VARCHAR(20) NOT NULL PRIMARY KEY
  ,champion_title     VARCHAR(30) NOT NULL
  ,class              VARCHAR(20) NOT NULL
  ,playstyle          VARCHAR(15) NOT NULL
  ,date_release       VARCHAR(15) NOT NULL
  ,blue_essence_price INTEGER  NOT NULL
  ,rp_price           INTEGER  NOT NULL
);
